/*Table structure for table `live_room` */

DROP TABLE IF EXISTS `live_room`;

CREATE TABLE `live_room` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `unique_id` bigint NOT NULL DEFAULT '0' COMMENT '唯一id',
  `live_type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '直播类型 1:录播 2-直播',
  `name` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '名称',
  `description` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '描述',
  `icon` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '直播间图标',
  `cover` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '直播间封面',
  `sort` int NOT NULL DEFAULT '0' COMMENT '排序 越小越靠前',
  `is_disabled` tinyint(1) NOT NULL DEFAULT '2' COMMENT '是否停用 1-停用 2-启用',
  `is_default` tinyint(1) NOT NULL DEFAULT '2' COMMENT '是否默认直播间 1:是 2:否',
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建日期',
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改日期',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_unique_id` (`unique_id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='直播间表';

